mapper.select:
SELECT * FROM notices
WHERE category = #{category}
AND notice_state = '공개'
ORDER BY is_important DESC, created_at DESC
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE notices
SET view_count = view_count + 1
WHERE notice_id = #{noticeId}

UPDATE notices
SET title = #{title},
    content = #{content},
    category = #{category},
    is_important = #{isImportant}
WHERE notice_id = #{noticeId}

mapper.delete:
UPDATE notices
SET notice_state = '비공개'
WHERE notice_id = #{noticeId}
